mapper.select:
		SELECT PHOTO, SCHOOL, MAJOR, CAREER, CERTIFICATE, SPECIALFIELD, TO_CHAR(PMDATE, 'YYYY-MM-DD') AS PMDATE, MENTORCNT, MENTOACCSTAR
  			 FROM PROFILE
  				WHERE MEMBERSEQ = #{memberseq}

mapper.update:
		UPDATE FINDINGMENTOR 
 			SET DELFLAG = 'Y'
 				WHERE BOARDSEQ = #{boardseq}

UPDATE NAEMEMBER 
	SET AUTH = 'ROLE_R'
		WHERE MEMBERSEQ =#{memberseq}

UPDATE NAEMEMBER
	SET MENTORTIER = 'B'
		WHERE MEMBERSEQ = #{memberseq}

UPDATE PROFILE
	SET FILECHK = 'N'
		WHERE MEMBERSEQ = #{memberseq}

mapper.select:
SELECT EMAIL
	FROM NAEMEMBER
		WHERE MEMBERSEQ = #{memberseq}

mapper.update:
		UPDATE PROFILE 
 			SET PMDATE = SYSDATE
 				WHERE MEMBERSEQ =#{memberseq}
