mapper.update:
UPDATE FINDINGMENTOR
SET MENTORLIST= CONCAT(MENTORLIST,#{memberseq})
WHERE BOARDSEQ=#{boardseq}

UPDATE FINDINGMENTOR
SET FINDREPORTER= CONCAT(FINDREPORTER,#{memberseq})
WHERE BOARDSEQ=#{boardseq}

	UPDATE FINDINGMENTOR SET WRITESDATE=SYSDATE
		
		,CONTENT=#{content}
		
		
		, SPECIALFIELD =#{specialfield}
		
		
		, TARGET=#{target}
		
		
		, MENTEELEVEL=#{menteelevel}
		
		
		, HOWTO=#{howto}
		
		
		, LOCATION=#{location}
		
		
		, MENTORINGDATE=#{mentoringdate}
		
		
  		WHERE BOARDSEQ = #{boardseq}

	UPDATE FINDINGMENTOR SET DELFLAG ='N'
 	WHERE MEMBERSEQ = #{memberseq} AND BOARDSEQ IN
 	
 		#{boardseq}

mapper.select:
SELECT p.MEMBERSEQ , n.EMAIL, NICKNAME , NVL(INTRODUCE, 0) INTRODUCE , PHONE , BIRTHDAY , GENDER , AUTH , USERSTATUS , NVL(MENTORTIER, 0) MENTORTIER, LOGINCNT , REPORTCNT, JOINDATE , MENTEECNT , MENTEEACCSTAR, NVL(BYEBYE,0) BYEBYE, NVL(MAJOR,0) MAJOR, NVL(SCHOOL,0) SCHOOL, NVL(CAREER,0) CAREER, NVL(CERTIFICATE,0) CERTIFICATE, NVL(SPECIALFIELD,0) SPECIALFIELD, TO_DATE(NVL(PMDATE, '21201231'), 'YYYY-MM-DD') PMDATE, MENTORCNT, MENTOACCSTAR, FILECHK, NVL(PHOTO,0) PHOTO FROM NAEMEMBER n JOIN PROFILE p ON n.MEMBERSEQ = p.MEMBERSEQ
p.MEMBERSEQ IN
#{memberseq}

SELECT f.BOARDSEQ, TITLE, CONTENT, WRITESDATE, REPORTCNT, SPECIALFIELD, TARGET, MENTEELEVEL, HOWTO, LOCATION, DELFLAG, MEMBERSEQ, FINDREPORTER, MENTORLIST, MATCHINGCHK, MENTORINGDATE FROM FINDINGMENTOR f JOIN MATCHING m ON f.BOARDSEQ = m.BOARDSEQ
WHERE m.MENTORSEQ =#{mentorseq}
WHERE m.MENTEESEQ =#{menteeseq}
AND DELFLAG = 'M'

SELECT f.BOARDSEQ, TITLE, CONTENT, WRITESDATE, REPORTCNT, SPECIALFIELD, TARGET, MENTEELEVEL, HOWTO, LOCATION, DELFLAG, MEMBERSEQ, FINDREPORTER, MENTORLIST, MATCHINGCHK, MENTORINGDATE FROM FINDINGMENTOR f JOIN MATCHING m ON f.BOARDSEQ = m.BOARDSEQ
WHERE m.MENTORSEQ =#{mentorseq}
WHERE m.MENTEESEQ =#{menteeseq}
AND DELFLAG = 'C'
