mapper.update:
	UPDATE NAEMEMBER 
       SET BYEBYE = 'Y'
          WHERE MEMBERSEQ = #{memberseq}

mapper.insert:
UPDATE PROFILE
SET PHOTO=#{photo}, SCHOOL=#{school}, MAJOR=#{major}, CAREER=#{career}, CERTIFICATE=#{certificate}, SPECIALFIELD=#{specialfield}
WHERE MEMBERSEQ=#{memberseq}

mapper.update:
UPDATE PROFILE
SET FILECHK='Y'
WHERE MEMBERSEQ=#{memberseq}
